import React, { useState, useEffect } from 'react';
import BotCollection from './BotCollection';

const YourBotArmy = () => {
  const [robots, setRobots] = useState([]);
  const [enlistedRobots, setEnlistedRobots] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3000/bots')
      .then((response) => response.json())
      .then(data => setRobots(data))
      .catch(err => console.log(err))
  }, []);

  console.log('All Robots:', robots);
  console.log('Enlisted Robots:', enlistedRobots);

  // Function to enlist a robot
  const enlistRobot = (robotId) => {
    // Assuming you have an API endpoint to enlist a robot
    fetch(`http://localhost:3000/bots/${robotId}/enlist`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ enlisted: true }),
    })
    .then((response) => response.json())
    .then((data) => {
      // Update the enlisted robot in the state
      setEnlistedRobots(prevEnlistedRobots => [...prevEnlistedRobots, data]);
    })
    .catch(err => console.log(err));
  };

  // Function to delete a robot
  const deleteRobot = (robotId) => {
    // Assuming you have an API endpoint to delete a robot
    fetch(`http://localhost:3000/bots/${robotId}`, {
      method: 'DELETE',
    })
    .then(() => {
      // Update the enlisted robot in the state
      setEnlistedRobots(prevEnlistedRobots => prevEnlistedRobots.filter(robot => robot.id !== robotId));
    })
    .catch(err => console.log(err));
  };

  return (
    <div>
      <h1>Your Bot Army</h1>
      <BotCollection robots={robots} enlistRobot={enlistRobot} deleteRobot={deleteRobot} />
      {enlistedRobots.length === 0 ? (
        <p>No robots enlisted yet.</p>
      ) : (
        <div>
          <h2>Enlisted Robots</h2>
          <ul>
            {enlistedRobots.map((robot) => (
              <li key={robot.id}>
                <h3>Name: {robot.name}</h3>
                <p>Health: {robot.health}</p>
                <p>Damage: {robot.damage}</p>
                <p>Armor: {robot.armor}</p>
                <p>Level: {robot.bot_class}</p>
                <button onClick={() => deleteRobot(robot.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default YourBotArmy;
