import React, { useState } from 'react';

const YourBotArmy = ({ bots }) => {
  const [army, setArmy] = useState([]);

  const enlistBot = (bot) => {
    // Check if the bot is already enlisted
    if (!army.some((enlistedBot) => enlistedBot.id === bot.id)) {
      setArmy((prevArmy) => [...prevArmy, bot]);
    }
  };

  const releaseBot = (bot) => {
    setArmy((prevArmy) => prevArmy.filter((enlistedBot) => enlistedBot.id !== bot.id));
  };

  const dischargeBot = (bot) => {
    // Perform any backend deletion here if needed

    setArmy((prevArmy) => prevArmy.filter((enlistedBot) => enlistedBot.id !== bot.id));
  };

  return (
    <div>
      <h1>Your Bot Army</h1>
      {army.length === 0 ? (
        <p>No bots enlisted yet. Enlist some now!</p>
      ) : (
        <ul>
          {army.map((bot) => (
            <li key={bot.id}>
              <h3>Name:{bot.name}</h3>
              <p>Health:{bot.health}</p>
              <p>Damage:{bot.damage}</p>
              <p>Armor:{bot.armor}</p>
              <p>Level:{bot.bot_class}</p>
              <button onClick={() => releaseBot(bot)}>Release</button>
              <button onClick={() => dischargeBot(bot)}>Discharge</button>
            </li>
          ))}
        </ul>
      )}
      <h2>Enlist a Bot</h2>
      <ul>
        {bots.map((bot) => (
          <li key={bot.id}>
            <h3>{bot.name}</h3>
            <p>{bot.health}</p>
            <p>{bot.damage}</p>
            <p>{bot.armor}</p>
            <button onClick={() => enlistBot(bot)}>Enlist</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default YourBotArmy;
